<template>
  <div
    class="fixed w-full text-white bg-dark shadow-header t-0 l-0 nav"
    style="top: 0 !important; z-index: 99999"
    @keydown.esc="closeModal"
  >
    <div class="flex items-center justify-between h-full container-auto">
      <div class="flex space-x-5 lg:space-x-0 logo">
        <button class="lg:hidden" @click="openMenu">
          <img src="/img/btn.svg" />
        </button>
        <NuxtLink to="/">
          <img src="/img/logo2.svg" alt="" />
        </NuxtLink>
      </div>
      <nav class="hidden space-x-8 lg:flex">
        <NuxtLink to="#aboutUs" class="link-h">О нас</NuxtLink>
        <NuxtLink to="#services" class="link-h">Услуги</NuxtLink>
        <NuxtLink to="#benefits" class="link-h">Преимущества</NuxtLink>
        <NuxtLink to="#delivery" class="link-h">Доставка</NuxtLink>
        <div class="cursor-pointer link-h" @click="toggleBuy">
          Закупка
          <span class="relative ml-2">
            <i
              v-if="buy"
              class="absolute fas fa-sort-up"
              style="top: 5px"
            ></i>
            <i
              v-else
              class="absolute fas fa-sort-down"
              style="top: -2px"
            ></i>
          </span>
          <transition
            enter-active-class="transition-transform duration-100 ease-out transform"
            leave-active-class="transition-transform duration-100 ease-in transform"
            enter-class="translate-y-8"
            leave-to-class="translate-y-8"
          >
            <Buy v-if="buy" />
          </transition>
        </div>
      </nav>
      <div class="relative">
        <button class="py-2 rounded-md z-1 px-7 bg-linear-fiolet hover:opacity-90" @click="openModal">
          Связаться
        </button>
      </div>
    </div>

    <transition
      enter-active-class="transition-opacity duration-100 ease-linear"
      leave-active-class="transition-opacity duration-100 ease-linear delay-75"
      enter-class="opacity-80"
      leave-to-class="opacity-80">

      <div
        v-show="modal"
        class="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-30"
        style="backdrop-filter: blur(20px)"
        @click="closeModal"
      >
        <transition
          enter-active-class="transition-transform duration-100 ease-out transform"
          leave-active-class="transition-transform duration-100 ease-in transform"
          enter-class="-translate-y-8"
          leave-to-class="-translate-y-8"
        >
          <CallBack v-show="modal" />
        </transition>
      </div>
    </transition>

    <transition
      enter-active-class="transition-opacity duration-100 ease-linear"
      leave-active-class="transition-opacity duration-100 ease-linear delay-75"
      enter-class="opacity-80"
      leave-to-class="opacity-80"
    >
      <div
        v-show="drawer"
        class="fixed inset-0 z-50 bg-opacity-30"
        style="backdrop-filter: blur(20px)"
        @click="closeMenu"
      >
        <transition
          enter-active-class="transition-transform duration-100 ease-out transform"
          leave-active-class="transition-transform duration-100 ease-in transform"
          enter-class="-translate-x-8"
          leave-to-class="-translate-x-8"
        >
          <div
            v-show="drawer"
            class="h-full overflow-y-scroll bg-dark"
            style="width: 300px;">

            <div class="flex items-center p-5 space-x-5 border-b border-gray-400">
              <button
                @click="closeMenu"
              >
                <img src="/img/closebtn.svg" alt="" />
              </button>

              <img src="/img/logo2.svg" alt="" />
            </div>
            <div class="flex flex-col items-start text-left">
              <NuxtLink
                to="/#aboutUs"
                class="px-5 py-3 hover:opacity-95"
                @click="closeMenu">О нас</NuxtLink>
              <NuxtLink
                to="/#services"
                class="px-5 py-3 hover:opacity-95"
                @click="closeMenu">Услуги</NuxtLink>
              <NuxtLink
